create table oehr_customers (
    customer_id     number(6, 0)
        generated by default on null as identity
    not null enable,
    cust_first_name varchar2(20 byte)
        constraint cust_fname_nn not null enable,
    cust_last_name  varchar2(20 byte)
        constraint cust_lname_nn not null enable,
    street_address  varchar2(40 byte),
    postal_code     varchar2(10 byte),
    city            varchar2(30 byte),
    state_province  varchar2(10 byte),
    country_id      char(2 byte),
    phone_number    varchar2(25 byte),
    nls_language    varchar2(3 byte),
    nls_territory   varchar2(30 byte),
    credit_limit    number(9, 2),
    cust_email      varchar2(30 byte),
    account_mgr_id  number(6, 0)
);

alter table oehr_customers
    add constraint oehr_customers_pk primary key ( customer_id )
        using index enable;

alter table oehr_customers add constraint oehr_customer_id_min check ( customer_id > 0 ) enable;

alter table oehr_customers
    add constraint oehr_cust_cl_max check ( credit_limit <= 5000 ) enable;


