create table oehr_employees (
    employee_id    number(6, 0)
        generated by default on null as identity
    not null enable,
    first_name     varchar2(20 byte),
    last_name      varchar2(25 byte)
        constraint oehr_emp_last_name_nn not null enable,
    email          varchar2(25 byte)
        constraint oehr_emp_email_nn not null enable,
    phone_number   varchar2(20 byte),
    hire_date      date
        constraint oehr_emp_hire_date_nn not null enable,
    job_id         varchar2(10 byte)
        constraint oehr_emp_job_nn not null enable,
    salary         number(8, 2),
    commission_pct number(2, 2),
    manager_id     number(6, 0),
    department_id  number(4, 0)
);

alter table oehr_employees add constraint oehr_emp_email_uk unique ( email )
    using index enable;

alter table oehr_employees
    add constraint oehr_emp_emp_id_pk primary key ( employee_id )
        using index enable;

alter table oehr_employees add constraint oehr_emp_salary_min check ( salary > 0 ) enable;


