create table oehr_order_items (
    order_id      number(12, 0),
    line_item_id  number(3, 0)
        constraint ord_item_line_item_nn not null enable,
    product_id    number(6, 0)
        constraint ord_item_prod_nn not null enable,
    unit_price    number(8, 2),
    quantity      number(8, 0),
    order_item_id number(12, 0)
        generated by default on null as identity
    not null enable
);

alter table oehr_order_items
    add constraint oehr_order_items_pk primary key ( order_item_id )
        using index enable;


