create table oehr_orders (
    order_id     number(12, 0)
        generated by default on null as identity
    not null enable,
    order_date   timestamp(6) with time zone
        constraint order_date_nn not null enable,
    order_mode   varchar2(8 byte),
    customer_id  number(6, 0)
        constraint oehr_order_customer_id_nn not null enable,
    order_status number(2, 0),
    order_total  number(8, 2),
    sales_rep_id number(6, 0),
    promotion_id number(6, 0)
);

alter table oehr_orders
    add constraint oehr_order_mode_lov
        check ( order_mode in ( 'direct', 'online' ) ) enable;

alter table oehr_orders
    add constraint oehr_order_pk primary key ( order_id )
        using index enable;

alter table oehr_orders
    add constraint oehr_order_total_min check ( order_total >= 0 ) enable;


