create table oehr_product_information (
    product_id          number(6, 0)
        generated by default on null as identity
    not null enable,
    product_name        varchar2(50 byte),
    product_description varchar2(2000 byte),
    category_id         number(2, 0),
    weight_class        number(1, 0),
    warranty_period     interval year(2) to month,
    supplier_id         number(6, 0),
    product_status      varchar2(20 byte),
    list_price          number(8, 2),
    min_price           number(8, 2),
    catalog_url         varchar2(50 byte)
);

alter table oehr_product_information
    add constraint oehr_product_info_pk primary key ( product_id )
        using index enable;

alter table oehr_product_information
    add constraint oehr_product_status_lov
        check ( product_status in ( 'orderable', 'planned', 'under development', 'obsolete' ) ) enable;


