create table oehr_warehouses (
    warehouse_id   number(3, 0)
        generated by default on null as identity
    not null enable,
    warehouse_name varchar2(35 byte),
    location_id    number(4, 0)
);

alter table oehr_warehouses
    add constraint oehr_warehouses_pk primary key ( warehouse_id )
        using index enable;


